"use strict";const arenas=document.querySelector(".arenas");class Player{constructor(name,hp,img=null,weapon=[]){this.name=name,this.hp=hp,this.img=img,this.weapon=weapon}attack(){console.log(this.name+" Fight...")}getPlayerDiv(className){return`\n    <div class="${className}">\n    <div class="progressbar">\n        <div class="life" style="width: ${this.hp}%"></div>\n        <div class="name">${this.name}</div>\n    </div>\n    <div class="character">\n        <img src="/assets/img/${this.name.toLowerCase()}.gif" />\n    </div>\n  </div>\n    `}}const scorpion=new Player("SCORPION",50),subZero=new Player("SUBZERO",80);function createPlayer(className,player){const playerDiv=document.createElement("DIV");arenas.appendChild(playerDiv),playerDiv.outerHTML=player.getPlayerDiv(className,player)}scorpion.attack(),subZero.attack(),createPlayer("player1",scorpion),createPlayer("player2",subZero);